import { useEffect,useState } from 'react';
import {Sidebar,Header,TaskList} from './index'
import axios from 'axios';

const Reminder = () =>{
  const[data,setData] = useState(['']);
  const[toggelMenu,setToggelMenu] = useState(false);
  useEffect(()=>{
    const getDatas = async ()=>{
      const response = await axios.get('http://localhost:3001/api',{});
      // console.log(response.data[0])
      setData(response.data)
    }
    getDatas()
  },[])

  const toggleMenuButton = () =>{
    setToggelMenu((prevState)=>{
      return !prevState
    })
    console.log(toggelMenu)
  }

  // const titles = ['title','title2','title3','title3','title4'];

  return(
    <>
    <Header toggleMenuButton={toggleMenuButton}/>
    <Sidebar title={data} checked={toggelMenu}/>
    <TaskList data={data} checked={toggelMenu}/>
    <button onClick={()=>{toggleMenuButton()}}>a</button>
    </>
  )
}

export default Reminder;
