import { useState,useEffect } from "react";

const AddTask = ({tasks}) =>{
  const [taskValue,setTaskValue] = useState({
    userId:0,id:0,task:''
  });

  useEffect(()=>{
    setTaskValue(tasks)
  },[tasks])

  return (
    <from className="addTask" method="post" action="/addTask">
      <input type="submit" />
    </from>
  )
}

export default AddTask;
